mapper.select:
     select count(*)
     from complain
	 where status=#{status}

select c.*,CONCAT(u.firstName,u.lastName) as userName,u.mobilePhone as userPhone, CONCAT(w.firstName,w.lastName) as waiterName,w.mobilePhone as waiterPhone from complain c left join user_info u on c.uid=u.uid left join user_info w on c.wid=w.uid
where c.status=#{status}
and c.comId like CONCAT(CONCAT('%',#{searchStr}),'%')
order by c.createDate desc
limit #{startRow},#{pageSize}
