update.if:
   			update market_menu set
			status=#{status},endDate=#{endDate}
			where mid=#{mid}

   			update market_menu set
			status=#{status},cancleDate=#{cancleDate}
			where mid=#{mid}

mapper.select:
      select count(*)
      from market_menu
      where 1=1

		and status=#{status}


		and smid=#{smid}


		and address like CONCAT(CONCAT('%',#{searchStr}),'%')


		and endDate between #{startDate} and #{endDate}

select mm.* ,m.name as marketName,concat(u.firstName,u.lastName) as userName,concat(u2.firstName,u2.lastName) as serverName from market_menu as mm left join market_info as m on mm.smid=m.smid left join user_info as u on mm.uid=u.uid left join user_info u2 on mm.wid=u2.uid where 1=1
and mm.status=#{status}
and mm.smid=#{smid}
and mm.address like CONCAT(CONCAT('%',#{searchStr}),'%')
and endDate between #{startDate} and #{endDate}
limit #{startRow},#{pageSize}

      select count(*) 
      from market_menu
      where 1=1

		and uid=#{searchStr}


		and wid=#{searchStr}

select mm.*,m.name as shopName,m.address as shopAddress,m.longitude as shopLongitude,m.latitude as shopLatitude, concat(u.firstName,u.lastName) as userName,u.logo as userLogo,u.mobilePhone as userPhone, concat(u2.firstName,u2.lastName) as serverName,u2.logo as serverLogo,u2.mobilePhone as serverPhone from market_menu as mm join market_info as m on mm.smid=m.smid join user_info as u on mm.uid=u.uid left join user_info u2 on mm.wid=u2.uid where 1=1
and mm.uid=#{searchStr}
and mm.wid=#{searchStr}
limit #{curPage},#{countPage}

mapper.update:
update market_menu set status=6,overTimeDate=#{updateDate}
where mealEndDate < #{mealEndDate}
and status=1
