mapper.select:
select count(*) from menu where 1=1

	and status=#{status}


	and sid=#{sid}


	and address like CONCAT(CONCAT('%',#{searchStr}),'%')


	and startDate=#{startDate}


	and endDate=#{endDate}


	select m.*,s.name as shopName,concat(u.firstName,u.lastName) as userName,concat(u2.firstName,u2.lastName) as serverName
	from menu m
	join shop_info s
	on m.sid=s.sid
	join user_info u
	on m.uid=u.uid
	left join user_info u2
    on m.wid=u2.uid
	where 1=1

		and m.status=#{status}


		and m.sid=#{sid}


		and m.address like CONCAT(CONCAT('%',#{searchStr}),'%')


		and m.startDate=#{startDate}


		and m.endDate=#{endDate}

	limit #{curPage},#{countPage}

select count(*) from menu where 1=1

	and uid=#{searchStr}


	and wid=#{searchStr}


select m.*,s.name as shopName,concat(u.firstName,u.lastName) as userName
from menu m
join shop_info s
on m.sid=s.sid
join user_info u
on m.uid=u.uid
where 1=1

	and m.uid=#{searchStr}


	and m.wid=#{searchStr}

limit #{curPage},#{countPage}

select m.*,s.name as shopName,concat(u.firstName,u.lastName) as userName
from menu m
join shop_info s
on m.sid=s.sid
join user_info u
on m.uid=u.uid
where m.uid=#{uid}

    and m.status=#{status}

limit #{curPage},#{countPage}

select count(*)
from menu
where uid=#{uid}

	and status=#{status}

mapper.update:
update menu set
mealStartDate=#{mealStartDate},mealEndDate=#{mealEndDate},updateBy=#{updateBy},updateDate=#{updateDate}
where mid=#{mid} and status=1
