mapper.select:
select count(c.id) from complain c left join user_info u on c.uid=u.uid where c.status=#{status}
and u.nickName like CONCAT(CONCAT('%', #{searchStr}),'%')
and c.updateDate between #{startDate} and #{endDate}

select c.*,u.nickName as userName,u.mobilePhone as userPhone, w.nickName as waiterName,w.mobilePhone as waiterPhone from complain c left join user_info u on c.uid=u.uid left join user_info w on c.wid=w.uid
where c.status=#{status}
and u.nickName like CONCAT(CONCAT('%', #{searchStr}),'%')
and c.updateDate between #{startDate} and #{endDate}
order by c.updateDate desc limit #{startRow},#{pageSize}
order by c.createDate desc
