update.if:
   			update menu set
			status=#{status},endDate=#{endDate}
			where mid=#{mid}

   			update menu set
			status=#{status},cancleDate=#{cancleDate}
			where mid=#{mid}

   			update menu set
			status=#{status},createDate=#{createDate}
			where mid=#{mid}

mapper.select:
select count(*) from menu where 1=1

	and status=#{status}


	and sid=#{sid}


	and address like CONCAT(CONCAT('%',#{searchStr}),'%')


	and endDate between #{startDate} and #{endDate}

select m.*,s.name as shopName,u.nickName as userName, u2.nickName as serverName from menu m left join shop_info s on m.sid=s.sid left join user_info u on m.uid=u.uid left join user_info u2 on m.wid=u2.uid where 1=1
and m.status=#{status}
and m.sid=#{sid}
and m.address like CONCAT(CONCAT('%',#{searchStr}),'%')
and endDate between #{startDate} and #{endDate}
limit #{curPage},#{countPage}

select count(*) from menu where 1=1

	and uid=#{searchStr}


	and wid=#{searchStr}


select m.*,s.name as shopName,u.nickName as userName
from menu m
join shop_info s
on m.sid=s.sid
join user_info u
on m.uid=u.uid
where 1=1

	and m.uid=#{searchStr}


	and m.wid=#{searchStr}

limit #{curPage},#{countPage}

select m.*,s.name as shopName,u.nickName as userName
from menu m
join shop_info s
on m.sid=s.sid
join user_info u
on m.uid=u.uid
where m.uid=#{uid}

    and m.status=#{status}

limit #{curPage},#{countPage}

select count(*)
from menu
where uid=#{uid}

	and status=#{status}

mapper.update:
update menu set
mealStartDate=#{mealStartDate},mealEndDate=#{mealEndDate},updateBy=#{updateBy},updateDate=#{updateDate}
where mid=#{mid} and status=1

update menu set status=6,overTimeDate=#{updateDate}
where mealEndDate < #{mealEndDate}
and status=1
